package com.linecomparion;

public class LineComparion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Line One
		int x1 = 10;
		int x2 = 20;
		int y1 = 30;
		int y2 = 40;
		
		double result = 0;
		result = Math.sqrt(Math.pow((x2 - x1), 2) + Math.pow((y2 - y1), 2)); 
		System.out.println(result);
	
	}

}
